import React, { useState, useEffect } from "react";
import "../../styles/weather.css";

const Weather = () => {
  const [weather, setWeather] = useState(null);
  const [forecast, setForecast] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    if ("geolocation" in navigator) {
      navigator.geolocation.getCurrentPosition(
        async (position) => {
          const { latitude, longitude } = position.coords;
          fetchWeather(latitude, longitude);
          fetchForecast(latitude, longitude);
        },
        (error) => {
          console.error("Error getting location:", error);
          setError("Failed to fetch location. Please allow location access.");
          setLoading(false);
        }
      );
    } else {
      setError("Geolocation is not supported by your browser.");
      setLoading(false);
    }
  }, []);

  const fetchWeather = async (lat, lon) => {
    try {
      const response = await fetch(
        `http://localhost:5000/api/weather?lat=${lat}&lon=${lon}`
      );
      const data = await response.json();
      setWeather(data);
    } catch (err) {
      console.error("Error fetching weather:", err);
      setError("Failed to fetch weather data.");
    } finally {
      setLoading(false);
    }
  };

  const fetchForecast = async (lat, lon) => {
    try {
      const response = await fetch(
        `http://localhost:5000/api/weather/forecast?lat=${lat}&lon=${lon}`
      );
      const data = await response.json();
      setForecast(data);
    } catch (err) {
      console.error("Error fetching forecast:", err);
      setError("Failed to fetch weather forecast.");
    }
  };

  return (
    <div className="weather-container">
      {loading && <p>Loading weather data...</p>}
      {error && <p className="error">{error}</p>}
      {weather && (
        <div className="weather-card">
          <h2>Current Weather in {weather.city}</h2>
          <p>üå°Ô∏è Temperature: {weather.temperature}¬∞C</p>
          <p>üíß Humidity: {weather.humidity}%</p>
          <p>üå¨Ô∏è Wind Speed: {weather.windSpeed} m/s</p>
          <p>üå¶Ô∏è Condition: {weather.description}</p>
        </div>
      )}
      
      {forecast.length > 0 && (
        <div className="forecast-section">
          <h3>5-Day Forecast</h3>
          <div className="forecast-cards">
            {forecast.map((day, index) => (
              <div key={index} className="forecast-card">
                <p>{day.date}</p>
                <p>üå°Ô∏è {day.temp}¬∞C</p>
                <p>üå¶Ô∏è {day.description}</p>
                <p>üíß {day.humidity}% Humidity</p>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default Weather;
