import { useState, useEffect } from "react";
import '../../styles/buyproduct.css';

const BuyProduct = () => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    fetch("http://localhost:5000/api/marketplace")
      .then((response) => response.json())
      .then((data) => setProducts(data));
  }, []);

  const handleCallSeller = (sellerContact) => {
    // Create a unique room ID based on seller's contact
    const roomId = `call-${sellerContact}`;

    // Notify seller about the call
    fetch("http://localhost:5000/api/call-request", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ roomId, sellerContact }),
    });

    // Redirect to VideoCall component
    window.location.href = `/videocall/${roomId}`;
  };

  return (
    <div className="buy-product-container">
      <h2>Available Products</h2>
      <div className="product-list">
        {products.map((product) => (
          <div key={product._id} className="product-card">
            <h3>{product.name}</h3>
            <p>Price: ₹{product.price}</p>
            <p>Seller: {product.seller}</p>
            <button onClick={() => handleCallSeller(product.sellerContact)}>
              Video Call Seller
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default BuyProduct;
