import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import { AuthProvider } from "./store/auth";
import ProtectedRoute from "./components/ProtectedRoute";
import Navbar from "./components/Navbar";
import Login from "./components/Auth/Login";
import Register from "./components/Auth/Register";
import Home from "./components/Home";
import RentEquipment from "./components/Equipment/RentEquipment";
import CropPrediction from "./components/CropPrediction/CropPrediction";
import Marketplace from "./components/Marketplace/Marketplace";
import WeatherForecast from "./components/Weather/Weather";
import ChatRoom from "./components/Chat/ChatRoom";
import AddWorker from "./components/Workers/AddWorker";
import AllWorkers from "./components/Workers/AllWorkers";
import Connect from "./components/Workers/Worker";
import { useState, useEffect, createContext } from "react";
import { translateText } from "./utils/translate";

// Translation Context
export const TranslationContext = createContext();

function App() {
  const [language, setLanguage] = useState("en");
  const [translatedTexts, setTranslatedTexts] = useState({});

  const textsToTranslate = {
    welcome: "Welcome to Krishi-Mitra 🌿",
    description: "Empowering Farmers with AI-Driven Solutions & Smart Agriculture",
    aboutTitle: "🚜 About Krishi-Mitra",
    aboutDescription:
      "Krishi-Mitra is a next-generation platform designed to revolutionize farming with AI and technology...",
    featuresTitle: "🌾 Key Features",
    connectWorkers: "👨‍🌾 Connect & Hire Workers",
    connectDescription: "Find skilled laborers in your area...",
    aiCropPrediction: "📊 AI-Powered Crop Prediction",
    marketplace: "🛒 Smart Marketplace with Video Calling",
    aiChat: "🤖 AI Chat Support",
    rentEquipment: "🚜 Rent Farming Equipment",
    weatherForecast: "🌦️ Weather Forecasting",
    readyToTransform: "🌱 Ready to Transform Your Farming Experience?",
    ctaDescription: "Join Krishi-Mitra today and take your agricultural practices to the next level...",
    getStarted: "Get Started",
    footer: "Krishi-Mitra: Empowering Farmers, One Step at a Time.",
  };

  useEffect(() => {
    const translateWebsite = async () => {
      const translations = {};
      for (const key in textsToTranslate) {
        translations[key] = await translateText(textsToTranslate[key], language);
      }
      setTranslatedTexts(translations);
    };

    translateWebsite();
  }, [language]);

  return (
    <TranslationContext.Provider value={{ language, setLanguage, translatedTexts }}>
      <Router>
        <AuthProvider>
          <Navbar />
          <Routes>
            <Route path="/login" element={<Login />} />
            <Route path="/register" element={<Register />} />
            <Route path="/" element={<Home />} />
            <Route path="/allworkers" element={<ProtectedRoute element={<AllWorkers />} />} />
            <Route path="/equipment" element={<ProtectedRoute element={<RentEquipment />} />} />
            <Route path="/crop-prediction" element={<ProtectedRoute element={<CropPrediction />} />} />
            <Route path="/marketplace" element={<ProtectedRoute element={<Marketplace />} />} />
            <Route path="/weather" element={<ProtectedRoute element={<WeatherForecast />} />} />
            <Route path="/chat" element={<ProtectedRoute element={<ChatRoom />} />} />
            <Route path="/addworkers" element={<ProtectedRoute element={<AddWorker />} />} />
            <Route path="/connect" element={<ProtectedRoute element={<Connect />} />} />
          </Routes>
        </AuthProvider>
      </Router>
    </TranslationContext.Provider>
  );
}

export default App;
