import { useState, useEffect } from "react";
import "../../styles/buyproduct.css";

const BuyProduct = () => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    fetch("http://localhost:5000/api/marketplace")
      .then((response) => response.json())
      .then((data) => setProducts(data));
  }, []);

  const handleVideoCall = async (sellerPhone, productName) => {
    if (!sellerPhone || !productName) {
      alert("Seller phone or product name is missing!");
      return;
    }

    try {
      const response = await fetch("http://localhost:5000/api/video-call/send-link", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ sellerPhone, productName }),
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || "Failed to create video call link");
      }

      // ✅ Open the meeting link for the customer automatically
      window.open(data.meetingLink, "_blank");

      alert(`Video call link sent to seller: ${data.meetingLink}`);

    } catch (error) {
      console.error("Error:", error);
      alert("Error starting video call.");
    }
  };

const handleBuy = async (productId, price, sellerEmail) => {
  try {
    const response = await fetch('http://localhost:5000/api/payment/payment', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ productId, price, sellerEmail }),
    });

    if (!response.ok) {
      throw new Error('Payment creation failed');
    }

    const data = await response.json();
    const razorpayOrderId = data.id;

    // Razorpay integration
    const options = {
      key: process.env.REACT_APP_RAZORPAY_KEY_ID,  // Make sure this is set in your .env file
      amount: price * 100, // Convert to paise
      currency: 'INR',
      order_id: razorpayOrderId,
      handler: function (response) {
        // Handle success (this would be a callback for successful payment)
        alert('Payment successful!');
        // Optionally, update the payment status in your backend
      },
      prefill: {
        name: 'Buyer Name',
        email: 'buyer@example.com',
        contact: '1234567890',
      },
      theme: {
        color: '#F37254',
      },
    };

    const rzp1 = new window.Razorpay(options);  // Ensure Razorpay instance is created correctly
    rzp1.open();  // Open the payment window

  } catch (error) {
    console.error('Payment error:', error);
  }
};

  return (
    <div className="buy-product-container">
      <h2>Available Products</h2>
      <div className="product-list">
        {products.map((product) => (
          <div key={product._id} className="product-card">
            <h3>{product.name}</h3>
            <p>Price: ₹{product.price}</p>
            <p>Seller: {product.seller}</p>
            <div className="button-group">
              <button onClick={() => handleBuy(product._id, product.price, product.seller)}>Buy Now</button>
              <button
                className="video-call-button"
                onClick={() => handleVideoCall(product.phone, product.name)}
              >
                Video Call Seller
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default BuyProduct;
