import '../../styles/sellproduct.css';
import { useState } from "react";

const SellProduct = () => {
  const [product, setProduct] = useState({
    name: "",
    price: "",
    description: "",
    seller: "",
    sellerContact: "",
    razorpayAccount: "",
    photo: null,
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setProduct({ ...product, [name]: value });
  };

  const handleFileChange = (e) => {
    setProduct({ ...product, photo: e.target.files[0] });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    Object.keys(product).forEach((key) => {
      formData.append(key, product[key]);
    });

    try {
      const response = await fetch("http://localhost:5000/api/marketplace/add", {
        method: "POST",
        body: formData,
      });

      if (response.ok) {
        alert("Product added successfully!");
      } else {
        alert("Failed to add product");
      }
    } catch (error) {
      alert("Error adding product");
    }
  };

  return (
    <form onSubmit={handleSubmit} className="sell-product-form">
      <input type="text" name="name" onChange={handleChange} placeholder="Product Name" required />
      <input type="number" name="price" onChange={handleChange} placeholder="Price" required />
      <textarea name="description" onChange={handleChange} placeholder="Description" required />
      <input type="text" name="seller" onChange={handleChange} placeholder="Your Name" required />
      <input type="text" name="sellerContact" onChange={handleChange} placeholder="Phone Number" required />
      <input type="text" name="razorpayAccount" onChange={handleChange} placeholder="Razorpay Account ID" required />
      <input type="file" name="photo" onChange={handleFileChange} required />
      <button type="submit">Upload Product</button>
    </form>
  );
};

export default SellProduct;
