import { useState } from "react";

const SellProduct = () => {
  const [product, setProduct] = useState({
    name: "",
    price: "",
    category: "",
    description: "",
    seller: "",
    ownerName: "",
    ownerContact: "",
    upi: "",
    bankAccount: "",
    photo: null,
  });
  const [message, setMessage] = useState(null);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setProduct({ ...product, [name]: value });
  };

  const handleFileChange = (e) => {
    setProduct({ ...product, photo: e.target.files[0] });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();

    // ✅ Append form fields properly
    Object.keys(product).forEach((key) => {
      if (product[key]) {
        formData.append(key, product[key]);
      }
    });

    try {
      const response = await fetch("http://localhost:5000/api/marketplace/add", {
        method: "POST",
        body: formData, // ✅ Sending formData (not JSON)
      });

      if (response.ok) {
        setMessage("Product added successfully!");
      } else {
        const errorData = await response.json();
        setMessage(errorData.message || "Failed to add product");
      }
    } catch (error) {
      console.error("Error adding product:", error);
      setMessage("Error adding product");
    }
  };

  return (
    <div className="sell-product-container">
      <h2>Sell Your Product</h2>
      {message && <p className="message">{message}</p>}
      <form onSubmit={handleSubmit} encType="multipart/form-data">
        <input type="text" name="name" placeholder="Product Name" onChange={handleChange} required />
        <input type="number" name="price" placeholder="Price (₹)" onChange={handleChange} required />
        <input type="text" name="category" placeholder="Category" onChange={handleChange} required />
        <textarea name="description" placeholder="Product Description" onChange={handleChange} required />
        <input type="text" name="seller" placeholder="Your Name" onChange={handleChange} required />
        <input type="text" name="ownerName" placeholder="Owner Name" onChange={handleChange} required />
        <input type="text" name="ownerContact" placeholder="Owner Contact" onChange={handleChange} required />
        <input type="text" name="upi" placeholder="UPI ID" onChange={handleChange} required />
        <input type="text" name="bankAccount" placeholder="Bank Account" onChange={handleChange} required />
        <input type="file" name="photo" onChange={handleFileChange} required />
        <button type="submit">Upload Product</button>
      </form>
    </div>
  );
};

export default SellProduct;
