import { useState, useEffect } from "react";
import JitsiMeetExternalAPI from "jitsi-meet-external-api"; // ✅ Import Jitsi API
import '../../styles/buyproduct.css';

const BuyProduct = () => {
  const [products, setProducts] = useState([]);
  const [videoRoom, setVideoRoom] = useState(null); // ✅ Store Jitsi room ID

  useEffect(() => {
    fetch("http://localhost:5000/api/marketplace")
      .then((response) => response.json())
      .then((data) => setProducts(data));
  }, []);

  const handleBuy = async (productId, price, sellerEmail) => {
    try {
      const response = await fetch('http://localhost:5000/api/payment/payment', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ productId, price, sellerEmail }),
      });

      if (!response.ok) {
        throw new Error('Payment creation failed');
      }

      const data = await response.json();
      const razorpayOrderId = data.id;

      const options = {
        key: process.env.REACT_APP_RAZORPAY_KEY_ID,
        amount: price * 100,
        currency: 'INR',
        order_id: razorpayOrderId,
        handler: function (response) {
          alert('Payment successful!');
        },
        prefill: {
          name: 'Buyer Name',
          email: 'buyer@example.com',
          contact: '1234567890',
        },
        theme: {
          color: '#F37254',
        },
      };

      const rzp1 = new window.Razorpay(options);
      rzp1.open();
    } catch (error) {
      console.error('Payment error:', error);
    }
  };

  const handleVideoCall = async () => {
    try {
      const response = await fetch("http://localhost:5000/api/video/generate-room");
      const data = await response.json();
      setVideoRoom(data.roomId);
    } catch (error) {
      console.error("Error generating video room:", error);
    }
  };

  return (
    <div className="buy-product-container">
      <h2>Available Products</h2>
      <div className="product-list">
        {products.map((product) => (
          <div key={product._id} className="product-card">
            <h3>{product.name}</h3>
            <p>Price: ₹{product.price}</p>
            <p>Seller: {product.seller}</p>
            <button onClick={() => handleBuy(product._id, product.price, product.seller)}>Buy Now</button>
            <button onClick={handleVideoCall}>Video Call Seller</button>
          </div>
        ))}
      </div>

      {videoRoom && (
        <div id="jitsi-container" style={{ width: "800px", height: "500px" }}>
          <script>
            {`
              var domain = "meet.jit.si";
              var options = {
                roomName: "${videoRoom}",
                width: 800,
                height: 500,
                parentNode: document.getElementById('jitsi-container')
              };
              var api = new JitsiMeetExternalAPI(domain, options);
            `}
          </script>
        </div>
      )}
    </div>
  );
};

export default BuyProduct;
