// components/Marketplace/BuyProduct.jsx
import { useState, useEffect } from "react";

const BuyProduct = () => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    fetch("http://localhost:5000/api/marketplace")
      .then((response) => response.json())
      .then((data) => setProducts(data));
  }, []);

  const handleBuy = async (productId, price, sellerEmail) => {
    try {
      const response = await fetch("http://localhost:5000/api/payment", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ productId, price, sellerEmail }),
      });

      const data = await response.json();
      window.location.href = data.url; // Redirect to payment
    } catch (error) {
      console.error("Payment error:", error);
    }
  };

  return (
    <div className="buy-product-container">
      <h2>Available Products</h2>
      <div className="product-list">
        {products.map((product) => (
          <div key={product._id} className="product-card">
            <h3>{product.name}</h3>
            <p>Price: â‚¹{product.price}</p>
            <p>Seller: {product.seller}</p>
            <button onClick={() => handleBuy(product._id, product.price, product.seller)}>Buy Now</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default BuyProduct;
