import { Link, useNavigate } from "react-router-dom";
import { useAuth } from "../store/auth"; // Import the Auth Context
import "../styles/navbar.css";

const Navbar = () => {
  const { user, logout } = useAuth(); // Get user state and logout function
  const navigate = useNavigate();

  const handleLogout = () => {
    logout(); // Use logout function from AuthProvider
    navigate("/login");
  };

  return (
    <nav className="navbar">
      <ul>
        <li><Link to="/">Home</Link></li>
        {user && (
          <>
            <li><Link to="/connect">Connect</Link></li>
            <li><Link to="/equipment">Rent Equipment</Link></li>
            <li><Link to="/crop-prediction">Crop Prediction</Link></li>
            <li><Link to="/marketplace">Marketplace</Link></li>
            <li><Link to="/weather">Weather</Link></li>
            <li><Link to="/chat">Chat</Link></li>
            <li><button onClick={handleLogout} className="logout-btn">Logout</button></li>
          </>
        )}
        {!user && <li><Link to="/login">Login</Link></li>}
      </ul>
    </nav>
  );
};

export default Navbar;
