import React, { useState, useEffect } from "react";
import "../../styles/weather.css";

const Weather = () => {
  const [weather, setWeather] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    if ("geolocation" in navigator) {
      navigator.geolocation.getCurrentPosition(
        async (position) => {
          const { latitude, longitude } = position.coords;
          fetchWeather(latitude, longitude);
        },
        (error) => {
          console.error("Error getting location:", error);
          setError("Failed to fetch location. Please allow location access.");
          setLoading(false);
        }
      );
    } else {
      setError("Geolocation is not supported by your browser.");
      setLoading(false);
    }
  }, []);

  const fetchWeather = async (lat, lon) => {
    try {
      const response = await fetch(
        `http://localhost:5000/api/weather?lat=${lat}&lon=${lon}`
      );
      const data = await response.json();
      setWeather(data);
    } catch (err) {
      console.error("Error fetching weather:", err);
      setError("Failed to fetch weather data.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="weather-container">
      {loading && <p>Loading weather...</p>}
      {error && <p className="error">{error}</p>}
      {weather && (
        <div className="weather-card">
          <h2>Weather in {weather.city}</h2>
          <p>üå°Ô∏è Temperature: {weather.temperature}¬∞C</p>
          <p>üå¨Ô∏è Wind Speed: {weather.windSpeed} m/s</p>
          <p>üíß Humidity: {weather.humidity}%</p>
          <p>üå§Ô∏è Condition: {weather.description}</p>
        </div>
      )}
    </div>
  );
};

export default Weather;
