import React, { useContext } from "react";
import { TranslationContext } from "../App";
import "../styles/home.css";

const Home = () => {
  const { translatedTexts } = useContext(TranslationContext);

  return (
    <div className="home-container">
      <header className="home-header">
        <h1>{translatedTexts.welcome || "Welcome to Krishi-Mitra 🌿"}</h1>
        <p>{translatedTexts.description || "Empowering Farmers with AI-Driven Solutions & Smart Agriculture"}</p>
      </header>

      <section className="home-about">
        <h2>{translatedTexts.aboutTitle || "🚜 About Krishi-Mitra"}</h2>
        <p>{translatedTexts.aboutDescription}</p>
      </section>

      <section className="home-features">
        <h2>{translatedTexts.featuresTitle}</h2>
        <div className="feature-grid">
          <div className="feature-card">
            <h3>{translatedTexts.connectWorkers}</h3>
            <p>{translatedTexts.connectDescription}</p>
          </div>
          <div className="feature-card">
            <h3>{translatedTexts.aiCropPrediction}</h3>
            <p>AI predicts crops based on soil and climate data.</p>
          </div>
        </div>
      </section>

      <section className="home-cta">
        <h2>{translatedTexts.readyToTransform}</h2>
        <p>{translatedTexts.ctaDescription}</p>
        <button className="cta-button">{translatedTexts.getStarted}</button>
      </section>

      <footer className="home-footer">
        <p>{translatedTexts.footer}</p>
      </footer>
    </div>
  );
};

export default Home;
