// components/BuyProduct.jsx
import { useState, useEffect } from 'react';

const BuyProduct = () => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    fetch('http://localhost:5000/api/marketplace')
      .then((response) => response.json())
      .then((data) => setProducts(data));
  }, []);

  const handleBuy = async (productId, price, sellerEmail) => {
    try {
      const response = await fetch('http://localhost:5000/api/marketplace/payment', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ productId, price, sellerEmail }),
      });

      const data = await response.json();
      window.location.href = data.paymentUrl; // Redirect to Razorpay payment page
    } catch (error) {
      alert('Error processing payment');
    }
  };

  return (
    <div>
      <h2>Available Products</h2>
      {products.map((product) => (
        <div key={product._id}>
          <h3>{product.name}</h3>
          <p>Price: â‚¹{product.price}</p>
          <button onClick={() => handleBuy(product._id, product.price, product.seller)}>Buy Now</button>
        </div>
      ))}
    </div>
  );
};

export default BuyProduct;
