import React, { useState, useEffect } from "react";
import axios from "axios";

const Weather = () => {
  const [weatherData, setWeatherData] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    navigator.geolocation.getCurrentPosition(
      async (position) => {
        const lat = position.coords.latitude;
        const lon = position.coords.longitude;

        try {
          const response = await axios.get(
            `http://localhost:5000/api/weather?lat=${lat}&lon=${lon}`
          );
          setWeatherData(response.data);
        } catch (err) {
          setError("Failed to fetch weather data");
        }
      },
      () => setError("Location access denied. Please allow location access.")
    );
  }, []);

  return (
    <div className="weather-container">
      {error ? (
        <p>{error}</p>
      ) : weatherData ? (
        <div>
          <h2>Weather in {weatherData.name || "Unknown Location"}</h2>
          <p>üå°Ô∏è Temperature: {weatherData.main?.temp}¬∞C</p>
          <p>üå¨Ô∏è Wind Speed: {weatherData.wind?.speed} m/s</p>
          <p>üíß Humidity: {weatherData.main?.humidity}%</p>
          <p>üå§Ô∏è Condition: {weatherData.weather?.[0]?.description}</p>
          <img
            src={`https://openweathermap.org/img/wn/${weatherData.weather?.[0]?.icon}.png`}
            alt="Weather Icon"
          />
        </div>
      ) : (
        <p>Loading weather data...</p>
      )}
    </div>
  );
};

export default Weather;
