import React, { useState } from "react"; // Ensure useState is imported
// import "../../styles/marketplace.css"; // Assuming you have a corresponding CSS file

const SellProduct = () => {
  const [product, setProduct] = useState({
    name: "",
    price: "",
    description: "",
    seller: "",
    photo: null,
    ownerDetails: {
      name: "",
      contact: "",
    },
  });
  const [message, setMessage] = useState(null);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setProduct({ ...product, [name]: value });
  };

  const handleFileChange = (e) => {
    setProduct({ ...product, photo: e.target.files[0] });
  };

  const handleOwnerDetailsChange = (e) => {
    const { name, value } = e.target;
    setProduct({
      ...product,
      ownerDetails: { ...product.ownerDetails, [name]: value },
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
  
    // Loop through the product data and append each field to the formData
    Object.keys(product).forEach((key) => {
      if (key === "ownerDetails") {
        Object.keys(product.ownerDetails).forEach((ownerKey) => {
          formData.append(`ownerDetails.${ownerKey}`, product.ownerDetails[ownerKey]);
        });
      } else {
        formData.append(key, product[key]);
      }
    });
  
    try {
      const response = await fetch("http://localhost:5000/api/marketplace/add", {
        method: "POST",
        body: formData, // Send the FormData as body
      });
  
      if (response.ok) {
        setMessage("Product added successfully!");
      } else {
        const errorData = await response.json();
        setMessage(errorData.message || "Failed to add product");
      }
    } catch (error) {
      setMessage("Error adding product");
      console.error("Error adding product:", error);
    }
  };
  

  return (
    <div className="sell-product-container">
      <h2>Sell Your Product</h2>
      {message && <p className="message">{message}</p>}
      <form onSubmit={handleSubmit}>
        <input type="text" name="name" placeholder="Product Name" onChange={handleChange} required />
        <input type="number" name="price" placeholder="Price (â‚¹)" onChange={handleChange} required />
        <textarea name="description" placeholder="Product Description" onChange={handleChange} required />
        <input type="text" name="seller" placeholder="Your Name" onChange={handleChange} required />
        <input type="text" name="ownerDetails.name" placeholder="Owner's Name" onChange={handleOwnerDetailsChange} required />
        <input type="text" name="ownerDetails.contact" placeholder="Owner's Contact" onChange={handleOwnerDetailsChange} required />
        <input type="file" name="photo" onChange={handleFileChange} required />
        <button type="submit">Upload Product</button>
      </form>
    </div>
  );
};

export default SellProduct;
