import { useState } from "react";
import { useNavigate } from "react-router-dom";
import '../../styles/adddworker.css'

const AddWorker = () => {
  const [worker, setWorker] = useState({
    name: "",
    phone: "",
    village: "",
    status: "Available", // Default status
    photo: "",
  });
  const [error, setError] = useState(null); // For storing any error messages
  const navigate = useNavigate();

  // Handle form input change
  const handleChange = (e) => {
    const { name, value } = e.target;
    setWorker((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!worker.name || !worker.phone || !worker.village || !worker.status) {
      setError("All fields are required!");
      return;
    }

    try {
      const response = await fetch("http://localhost:5000/api/workers", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(worker),
      });

      if (response.ok) {
        navigate("/workers"); // Redirect to workers list page after adding
      } else {
        const errorData = await response.json();
        setError(errorData.message || "Failed to add worker");
      }
    } catch (error) {
      setError("An error occurred. Please try again.");
      console.error(error);
    }
  };

  return (
    <div className="add-worker-container">
      <h2>Add Worker</h2>
      {error && <div className="error-message">{error}</div>} {/* Show error */}
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="name"
          placeholder="Name"
          onChange={handleChange}
          value={worker.name}
          required
        />
        <input
          type="text"
          name="phone"
          placeholder="Phone"
          onChange={handleChange}
          value={worker.phone}
          required
        />
        <input
          type="text"
          name="village"
          placeholder="Village"
          onChange={handleChange}
          value={worker.village}
          required
        />
        <select
          name="status"
          onChange={handleChange}
          value={worker.status}
          required
        >
          <option value="Available">Available</option>
          <option value="Busy">Busy</option>
          <option value="Inactive">Inactive</option>
        </select>
        <input
          type="text"
          name="photo"
          placeholder="Photo URL"
          onChange={handleChange}
          value={worker.photo}
        />
        <button type="submit">Add Worker</button>
      </form>
    </div>
  );
};

export default AddWorker;
