import { Link } from "react-router-dom";
import { useAuth } from "../store/auth";
import { useContext } from "react";
import { LanguageContext } from "../App";

const Navbar = () => {
  const { user, logout, isAuthenticated } = useAuth();
  const { language, translateWebsite, translatedText } = useContext(LanguageContext);

  return (
    <nav className="navbar">
      <ul>
        <li><Link to="/">{translatedText.home || "Home"}</Link></li>
        {isAuthenticated ? (
          <>
            <li><Link to="/connect">{translatedText.connect || "Connect"}</Link></li>
            <li><Link to="/equipment">{translatedText.rentEquipment || "Rent Equipment"}</Link></li>
            <li><Link to="/crop-prediction">{translatedText.cropPrediction || "Crop Prediction"}</Link></li>
            <li><Link to="/marketplace">{translatedText.marketplace || "Marketplace"}</Link></li>
            <li><Link to="/weather">{translatedText.weather || "Weather"}</Link></li>
            <li><Link to="/chat">{translatedText.chat || "Chat"}</Link></li>
            <li><button onClick={logout} className="logout-btn">{translatedText.logout || "Logout"}</button></li>
          </>
        ) : (
          <li><Link to="/login">{translatedText.login || "Login"}</Link></li>
        )}
      </ul>

      {/* Language Selector Dropdown */}
      <select value={language} onChange={(e) => translateWebsite(e.target.value)}>
        <option value="en">English</option>
        <option value="mr">मराठी (Marathi)</option>
        <option value="hi">हिंदी (Hindi)</option>
        <option value="es">Español (Spanish)</option>
        <option value="fr">Français (French)</option>
      </select>
    </nav>
  );
};

export default Navbar;
