// components/SellProduct.jsx
import { useState } from 'react';

const SellProduct = () => {
  const [product, setProduct] = useState({
    name: '',
    price: '',
    description: '',
    seller: '',
    photo: null,
    ownerName: '',          // Owner's name (Farmer's name)
    ownerContact: '',       // Owner's contact (Farmer's contact)
    razorpayAccount: '',    // Razorpay account for payment
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setProduct({ ...product, [name]: value });
  };

  const handleFileChange = (e) => {
    setProduct({ ...product, photo: e.target.files[0] });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    Object.keys(product).forEach((key) => {
      formData.append(key, product[key]);
    });

    try {
      const response = await fetch('http://localhost:5000/api/marketplace/add', {
        method: 'POST',
        body: formData,
      });

      if (response.ok) {
        alert('Product added successfully!');
      } else {
        alert('Failed to add product');
      }
    } catch (error) {
      alert('Error adding product');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        name="name"
        value={product.name}
        onChange={handleChange}
        placeholder="Product Name"
        required
      />
      <input
        type="number"
        name="price"
        value={product.price}
        onChange={handleChange}
        placeholder="Price"
        required
      />
      <textarea
        name="description"
        value={product.description}
        onChange={handleChange}
        placeholder="Description"
        required
      />
      <input
        type="text"
        name="seller"
        value={product.seller}
        onChange={handleChange}
        placeholder="Your Name"
        required
      />
      <input
        type="text"
        name="ownerName"
        value={product.ownerName}
        onChange={handleChange}
        placeholder="Owner's Name (Farmer's name)"
        required
      />
      <input
        type="text"
        name="ownerContact"
        value={product.ownerContact}
        onChange={handleChange}
        placeholder="Owner's Contact"
        required
      />
      <input
        type="text"
        name="razorpayAccount"
        value={product.razorpayAccount}
        onChange={handleChange}
        placeholder="Razorpay Account Details"
        required
      />
      <input
        type="file"
        name="photo"
        onChange={handleFileChange}
        required
      />
      <button type="submit">Upload Product</button>
    </form>
  );
};

export default SellProduct;
